mapper.select:
select count(*)
from eden_user
WHERE user_id = #{user_id}

select count(*)
from eden_user
where user_nickname = #{user_nickname}

select count(*)
from eden_user
where user_phone = #{user_phone}

select count(*)
from eden_user
where user_email = #{user_email}

select *
from eden_user
where user_nickname = #{user_nickname}
  AND user_email = #{user_email}

SELECT *
FROM eden_user
WHERE user_findAnswer = #{user_findAnswer}
  AND user_id = #{user_id}

mapper.update:
UPDATE eden_user
SET user_pw = #{user_pw}
WHERE user_id = #{user_id}

UPDATE eden_user
SET user_status = 'Inactive'
WHERE user_no = #{user_no}
  AND user_pw = #{user_pw}
